        setActive(!isActive);
    };
    function del(index) {
        console.log(index);
        setPosts([...posts.slice(0, index), ...posts.slice(index + 1)]);
    }
  return (
    <Posts>
      <Cross onClick={props.tog}>+</Cross>
      <Title>Cody</Title>
      <span>{props.text}</span>

      <Likes>
        <Heart onClick={liking} src={heart} alt="heart" /> {likes}
      </Likes>
        <Mymodal isActive={isActive} posts={posts} del={del} ind={props.ind}/>
    </Posts>
  );
};

export default Post;
