import React,{useState} from "react";
import {Open, Close,Modal,Butt} from './myModal.js';




const Mymodal = (props) => {
  const [del, setDel] = useState(false)
  function delchange (){
    setDel(!del)
    console.log('ffff')
  }
  return (
    <div>
      {props.isActive ? (
        <Open>
          <Modal>
            Вы действительно хотите удалить пост?
            <Butt onClick={delchange}> Да</Butt>
            <Butt> Нет</Butt>
          </Modal>
        </Open>
      ) : (
        <Close />
      )}
    </div>
  );
};

export default Mymodal;
