import React, { useState } from 'react';
import { useEffect } from 'react';
import Mymodal from '../../components/modal/myModal.jsx';
import Post from '../../components/posts/Post.jsx';
import Nav from '../../components/navigation/Nav.jsx';
import Myinput from '../../components/input/myInput.jsx';

function MainPage() {
  const [text, setText] = useState('');

  const [posts, setPosts] = useState(() => {
    const saved = localStorage.getItem('name');
    const initialValue = JSON.parse(saved);
    return initialValue || [];
  });

  useEffect(() => {
    localStorage.setItem('name', JSON.stringify(posts));
  }, [posts]);

  function AddPost(e) {
    e.preventDefault();
    const obj = {
      text,
    };
    setPosts([...posts, obj]);
  }

  function del(index) {
    setPosts([...posts.slice(0, index), ...posts.slice(index + 1)]);
  }

  const [isActive, setActive] = useState(false);

  const toggleClass = () => {
    setActive(!isActive);
  };

  return (
    <div className="App">
      <Nav />

      {posts.map((e, index) => (
        <Post
          key={index}
          text={e.text}
          del={() => del(index)}
          tog={toggleClass}
        />
      ))}
      <Myinput
        value={text}
        onchange={(e) => setText(e.target.value)}
        onclick={AddPost}
      />
      <Mymodal isActive={isActive} posts={posts} dell={del} />
    </div>
  );
}

export default MainPage;
