    setPosts([...posts.slice(0, index), ...posts.slice(index + 1)]);
  } else { console.log('hhhhh')}
  }

  const [isActive, setActive] = useState(false);

  const toggleClass = () => {
    setActive(!isActive);
  };
  const [dell, setDel] = useState(false)
  function delchange (){
    setDel(!dell)
    console.log('ffff')
  }
  return (
    <div className="App">
      <Nav />
      <Myinput
        value={text}
        onchange={(e) => setText(e.target.value)}
        onclick={AddPost}
      />

      {posts.map((e, index) => (
        <Post
          key={index}
          text={e.text}
          tog={toggleClass}
        />
      ))}

      <Mymodal isActive={isActive} posts={posts} delett={delchange} />
    </div>
  );
}

export default MainPage;
