import React from "react";
import heart from "../../assets/img/heart.png";
import { useState } from "react";
import './Post.css';
import styled from "styled-components";

const Posts = styled.div`
width: 50%;
position: relative;
display: flex;
flex-direction: column;
overflow: hidden;
margin: 0 25% 0 25%;
border: solid;
border-color: rgb(212, 197, 247);
margin-bottom: 30px;
word-wrap: break-word;
background-color: rgb(240, 234, 253);
`
const Cross = styled.span`
margin: 2px 7px 0 7px;
`
const Title = styled.h1`
font-size: 17px;
  display: block;
  margin: 7px 0 0 5px;
`


const Post = (props) => {

  const [likes, setLikes] = useState(0);

  function liking() {
    setLikes(likes + 1);
    console.log(likes);
  }

  return (
    <Posts>
      <Cross onClick={props.tog}>+</Cross> 
      
    <h1>Cody</h1>
    <span>{props.text}</span>

    <div className="likes">
      <img onClick={liking} className="heart" src={heart} alt="heart" />
      {likes}
    </div></Posts>
     
   
  );
};

export default Post;
