import React from "react";
import heart from "../../assets/img/heart.png";
import { useState } from "react";
import './Post.css';
import styled from "styled-components";

const Posts = styled.div`
width: 50%;
position: relative;
display: flex;
flex-direction: column;
overflow: hidden;
margin: 0 25% 0 25%;
border: solid;
border-color: rgb(212, 197, 247);
margin-bottom: 30px;
word-wrap: break-word;
background-color: rgb(240, 234, 253);

`

const Post = (props) => {

  const [likes, setLikes] = useState(0);

  function liking() {
    setLikes(likes + 1);
    console.log(likes);
  }

  return (
    <Posts> 
      <span className="delete" onClick={props.tog}>+</span>
    <h1>Cody</h1>
    <span>{props.text}</span>

    <div className="likes">
      <img onClick={liking} className="heart" src={heart} alt="heart" />
      {likes}
    </div></Posts>
     
   
  );
};

export default Post;
