import styled from 'styled-components';

export const Area = styled.textarea`
  width: 51%;
  margin: 0 0 0 25%;
  resize: none;

  border-color: black;
  height: 60px;
`;
export const Button = styled.button`
  margin: 0 0 0 66.5%;
  background: rgb(212, 197, 247);
  border: 1px solid;
  width: 10%;
  height: 30px;
`;
